import { BOX_SIZE } from '@sudoku/constants';

function formatTime(time) {
	let str = new Date(time * 1000).toISOString().substr(11, 8);

	if (str.substr(0, 2) === '00') str = str.substr(3);

	return str;
}

function getBoxPos(x, y) {
	return {
		x: Math.floor(x / BOX_SIZE),
		y: Math.floor(y / BOX_SIZE)
	}
}

export {
	formatTime,
	getBoxPos
};