import { SUDOKU_SIZE } from '@sudoku/constants';
import { writable } from 'svelte/store';

function createCursor() {
	const cursor = writable({ x: 0, y: 0 });

	return {
		subscribe: cursor.subscribe,

		set(x, y) {
			cursor.set({ x, y });
		},

		move(xDir = 0, yDir = 0) {
			cursor.update($cursor => {
				let newX = $cursor.x + xDir;
				let newY = $cursor.y + yDir;

				if (newX <= 0) newX = SUDOKU_SIZE;
				if (newX > SUDOKU_SIZE) newX = 1;
				if (newY <= 0) newY = SUDOKU_SIZE;
				if (newY > SUDOKU_SIZE) newY = 1;

				return {
					x: newX,
					y: newY,
				};
			});
		},

		reset() {
			this.set(0, 0);
		}
	};
}

export const cursor = createCursor();