import { cursor } from '@sudoku/stores/cursor';
import { grid } from '@sudoku/stores/grid';
import { timerRunning } from '@sudoku/stores/timer';
import { derived } from 'svelte/store';

export const keyboardDisabled = derived([cursor, grid, timerRunning], ([$cursor, $grid, $timerRunning]) => {
	return cursor.isInitial($cursor) || $grid[$cursor.y - 1][$cursor.x - 1] !== 0 || !$timerRunning;
}, false);