import { timer } from '@sudoku/stores/timer';
import { writable } from 'svelte/store';
import { MODAL_NONE } from '@sudoku/constants';

export const modalData = writable({});

function createModal() {
	let startTimerOnHide = false;

	const modalType = writable(MODAL_NONE);

	modalData.subscribe(data => {
		startTimerOnHide = data.startTimerOnHide;
	});

	return {
		subscribe: modalType.subscribe,

		show: (type, data = {}) => {
			modalType.set(type);
			modalData.set(data);
		},

		hide: () => {
			modalType.set(MODAL_NONE);

			if (startTimerOnHide) {
				setTimeout(() => timer.start(), 100);
			}
		},
	};
}

export const modal = createModal();