console.log(candidates)



function stringToMatrix(str) {
    if (str.length!== 81) {
        throw new Error('The input string must be 81 characters long.');
    }

    const matrix = [];
    for (let i = 0; i < 9; i++) {
        matrix[i] = [];
        for (let j = 0; j < 9; j++) {
            matrix[i][j] = parseInt(str[i * 9 + j]);
        }
    }

    return matrix;
}